import useInput from "../hooks/use-input";


const BasicForm = (props) => {
  useInput();
  const formValid = false;


  const fnameInputClass = fnameIsInvalid? 'form-control invalid':'form-control'
  const lnameInputClass = lnameIsInvalid? 'form-control invalid':'form-control'
  const emailInputClass = emailIsInvalid? 'form-control invalid':'form-control'

  return (
    <form>
      <div className='control-group'>
        <div className={fnameInputClass}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={fnameChangeHandler} value={fname} />
        </div>
        <div className={lnameInputClass}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lnameChangeHandler} value={lname} />
        </div>
      </div>
      <div className={emailInputClass}>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' onChange={emailChangeHandler} value={email} />
      </div>
      <div className='form-actions'>
        <button disabled={!formValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
