import { useState } from "react";

const SimpleInput = (props) => {
  const [name, setName] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [email, setEmail] = useState("");
  const [emailTouched, setEmailTouched] = useState(false);

  const nameIsValid = name.trim() !== "";
  const nameIsInvalid = !nameIsValid && nameTouched;
  const emailIsValid = email.trim() !== "" && email.match('@.');
  const emailIsInvalid = !emailIsValid && emailTouched;
  let formValid = false;

  formValid = nameIsValid && emailIsValid;

  const inputBlurHandler = () => {
    setNameTouched(true);
    console.log("Name Touched");
  };

  const inputChangeHandler = (e) => {
    setName(e.target.value);
  };

  const emailBlurHandler = () => {
    setEmailTouched(true);
    console.log("Email Touched");
  };

  const emailChangeHandler = (e) => {
    setEmail(e.target.value);
  };

  const formSubmitHandler = (e) => {
    e.preventDefault();

    setNameTouched(true);
    setEmailTouched(true);

    if (!nameIsValid) {
      return;
    }

    if (!emailIsValid) {
      return;
    }

    console.log(name);
    console.log(email);
    setName("");
    setEmail("");
    setNameTouched(false);
    setEmailTouched(false);
  };

  const nameInputClass = nameIsInvalid
    ? "form-control invalid"
    : "form-control";

  const emailInputClass = emailIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputChangeHandler}
          onBlur={inputBlurHandler}
          value={name}
        />
        {nameIsInvalid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className={emailInputClass}>
        <label htmlFor="name">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
          value={email}
        />
        {emailIsInvalid && (
          <p className="error-text">Email must be valid.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
